INSERT INTO `q_questions` VALUES ('843', '中央处理器CPU的主要功能是(    )。', '分析并执行指令', '计算数据', '存储数据', '输出数据', '', '', '', 'A', '中央处理器（CPU）是计算机主机的核心部件，主要功能是按照程序给出的指令序列，分析并执行指令。\r\n', '1', '1', '2', '106', '2017-12-13 20:44:52', '2017-12-13 20:44:52', null, null);
