INSERT INTO `q_questions` VALUES ('2437', '下列有关会计软件的叙述正确的有(    )。', '会计软件对会计资料进行转换、输出、分析和利用', '会计软件是专门用于会计核算和财务管理的计算机软件', '会计软件以管理和控制计算机系统资源的运行为任务', '会计软件为会计核算和财务管理直接提供数据输入', '', '', '', 'ABD', '会计软件是指专门用于会计核算、财务管理的计算机软件、软件系统及其功能模块，包括一组指挥计算机进行会计核算与管理工作的程序、存储数据以及有关资料。通常具有以下主要功能1.为会计核算、财务管理直接提供数据输入；2.生成凭证、账簿、报表等会计资料；3.对会计资料进行转换、输出、分析、利用。', '1', null, '2', '105', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
