INSERT INTO `q_questions` VALUES ('3223', '常见的输出设备有(    )。', '键盘', '显示器', '打印机', '鼠标', '', '', '', 'BC', '本题考核输出设备的内容。键盘和鼠标是输入设备，显示器和打印机是输出设备，因此选择BC正确。\r\n', '2', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
