INSERT INTO `q_questions` VALUES ('3226', '外存储器在微机中通常不包括(    )。', 'U盘', '磁盘存储器', '硬盘存储器', '光盘存储器', '', '', '', 'B', '外存储器一般存储容量较大，但数据存取速度较慢。常见的外存储器有硬盘、U盘、光盘等。', '1', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3227', '多用户结构的主机与终端的距离为(    )左右。', '0', '1千米', '10千米', '300千米', '', '', '', 'A', '多用户结构中，主机和终端的距离较近（0.1千米左右）。', '1', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3228', '客户机/服务器客户端的作用是(    )。', '会计数据的定义', '会计数据的存储', '会计数据的备份', '会计数据的输入和输出', '', '', '', 'D', '客户机/服务器模式下，客户端安装专用的会计软件，负责会计数据的输入、运算和输出。', '1', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3229', '若计算机在工作过程中电源突然中断，则计算机中将全部丢失的是(    )。', 'RM中的信息', 'ROM中的信息', '硬盘中的信息', 'ROM与RM中的信息', '', '', '', 'A', '内存储器即内存，分为随机存储器RAM（Random Access Memory）和只读存储器ROM（Read-On1y Memory），一般容量较小，但数据存取速度较快。断电后，RAM的数据将会消失。', '1', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3230', '随机内存储器的特点有(    )。', '存储容量小', '存储速度快', '存储容量大', '断电后数据消失', '', '', '', 'ABD', '随机存储器RAM存储容量较小，但数据存取速度较快，断电后，RAM的数据将会消失。', '2', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3231', '在会计软件中，下列属于显示器可以显示的信息有(    )。', '输入的命令', '输入的信息', '会计数据', '文件', '', '', '', 'ABCD', '显示器既可以显示用户在系统中输入的各种命令和信息，也可以显示系统生成的各种会计数据和文件。', '2', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3232', '客户机/服务器结构模式下，服务器的作用有(    )。', '负责会计数据的定义', '负责会计数据的存储', '负责会计数据的备份', '负责会计数据的恢复', '', '', '', 'ABCD', '客户机/服务器结构模式下，服务器负责会计数据的定义、存取、备份和恢复，客户端安装专用的会计软件，负责会计数据的输入、运算和输出。\r\n', '2', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3233', '微机局域网络通常分为(    )结构。', '客户机/服务器', '浏览器/服务器', '客户机/浏览器', '浏览器/客户机', '', '', '', 'AB', '微机局域网络通常分为客户机／服务器结构和浏览器／服务器结构两种结构。\r\n', '2', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3234', '多用户结构系统配置(    )。', '一台主机', '多台主机', '一个终端', '多个终端', '', '', '', 'AD', '多用户结构，整个系统配备一台计算机主机和多个终端。\r\n', '2', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3235', '下列属于多用户结构的优点是(    )。', '应用软件多', '费用低', '数据分散输入', '主机负载小', '', '', '', 'C', '多用户结构的优点在于会计数据可以通过各终端分散输入，并集中存储和处理。\r\n', '1', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3236', '中央处理器CPU的主要功能是(    )。', '分析并执行指令', '计算数据', '存储数据', '输出数据', '', '', '', 'A', '中央处理器（CPU）是计算机主机的核心部件，主要功能是按照程序给出的指令序列，分析并执行指令。\r\n', '1', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3237', '内存储器有随机存储器和(    )。', 'RM', '磁盘存储器', '只读存储器', '磁带存储器', '', '', '', 'C', '内存储器即内存，分为随机存储器RAM（Random Access Memory）和只读存储器ROM（Read-On1y Memory）。', '1', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3238', 'U盘属于(    )存储器。', '内部存储器', '高速缓冲存储器', '只读存储器', '外部移动存储器', '', '', '', 'D', 'U盘属于外部移动存储器。高速缓冲存储器 、只读存储器均属于内存储器。\r\n', '1', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3239', '有多台微机，但每台微机都有相应的输入输出设备，每台微机仍属单机结构，各台微机不发生直接的数据联系，以上描述的是(    )。', '单机结构', '多用户结构', '多机松散结构', '微机局域网络', '', '', '', 'C', '多机松散结构是指有多台微机，但每台微机都有相应的输入输出设备，每台微机仍属单机结构，各台微机不发生直接的数据联系（通过磁盘、光盘、U盘、移动硬盘等传送数据）。', '1', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3240', '存储器可以分为(    )。', '基本存储器', '辅助存储器', '外存储器', '内存储器', '', '', '', 'CD', '计算机的存储设备包括内存储器和外存储器。\r\n', '2', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3241', '浏览器/服务器结构的工作特点有(    )。', '服务器是实现会计功能软件的核心', '客户机上只安装浏览器程序', '用户通过浏览器向服务器发出请求', '服务器对浏览器的请求进行处理', '', '', '', 'ABCD', '浏览器/服务器结构模式下，服务器是实现会计软件功能的核心部分，客户机上只需安装一个浏览器，用户通过浏览器向分布在网络上的服务器发出请求，服务器对浏览器的请求进行处理，将用户所需信息返回到浏览器。\r\n', '2', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
INSERT INTO `q_questions` VALUES ('3242', '在会计软件中，显示器的作用有(    )。', '显示输入的各种命令', '显示输入的信息', '显示系统生成的会计数据', '显示系统生成的文件', '', '', '', 'ABCD', '显示器既可以显示用户在系统中输入的各种命令和信息，也可以显示系统生成的各种会计数据和文件。', '2', null, '2', '106', '2017-12-13 20:42:49', '2017-12-13 20:42:49', null, null);
