mapper.insert:
insert into q_course (id, parent_id, name, 
  type, status, sort_order, 
  update_time, create_time, delete_time
  )
values (#{id,jdbcType=INTEGER}, #{parentId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{type,jdbcType=INTEGER}, #{status,jdbcType=BIT}, #{sortOrder,jdbcType=INTEGER}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}, #{deleteTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT

FROM q_course
WHERE parent_id = #{parent_id}
and type = #{type}

sql.where:

  and (
  LOWER(name) like  LOWER(CONCAT("%",#{findContent,jdbcType=VARCHAR},"%"))
  )

and delete_time is null

  and parent_id = #{course_id}


  and parent_id = 0
